import { useState , useRef ,useEffect, Fragment} from 'react';
import axios from 'axios';
import Flight from '../Flight/Flight';
import './List.css';
const List = ()=> {
  
  const [list,setlist] = useState([]);
  const [isLoading, setLoading] = useState(true);
  const [formStatus, setformStatus] = useState("1"); 
  const [Cart,setCart] = useState([]);
  const [Data,setData] = useState([])
  
  useEffect(()=>{
    axios.get('http://localhost:8000/flight/')
    .then(function (response) {
     setlist(response.data) 
     setData(response.data) 
     setLoading(false)
    })

  },[]);


  const inputChange = event => {

    if(event.target.value == ""){
      setlist(Data)
      console.log("test")
    return;
    }
    let result = [];
    if(isNaN(event.target.value)){
      Data.forEach(item =>{
        if(item.name.toLowerCase().includes(event.target.value)){result.push(item)}
        if(item.category.toLowerCase().includes(event.target.value)){result.push(item)}
       })
       setlist(result); 
    }else{
      Data.forEach(item =>{
        if(parseInt(item.price) <parseInt(event.target.value)){result.push(item)
          console.log(event.target.value)
        console.log(item.price)}
      })
      setlist(result); 
    }

  };
const addToCart =((item)=>{
console.log(item);
const i = Cart.findIndex(e => e.name === item.name);
if(i==-1){
  setCart ( (Cart ) => {
    return [item , ...Cart];
  
  });
}



})
 
 if(isLoading) {
  return (
    <h1>Loading</h1>
      )
 }else{
  if(formStatus==1){
    return( 
      <Fragment>
      
        <div>
           <div id="main-navbar" className="navbar" >
             <h2 className="logo">Login to watch out flights or sign-up if you don't have an account</h2>
             <nav>
               <ul>
               <li  onClick={()=>setformStatus("1")}>
                 Flights
               </li>
               <li onClick={()=>setformStatus("2")}>
                 Cart
               </li>
              
               </ul>
             </nav>
           </div>
         </div>
       
         <input id="filter"  onChange={inputChange} placeholder="Serach flight by the location name,mainland or max price "></input>
        <section class="cards">
           {list.map( (flight )  => {
            return(
              <div>
           <Flight flight={flight} ></Flight> 
              <button onClick={()=>addToCart(flight)}> Add to Cart</button>
              </div>
            
            )
           }
           
           )}
       </section>
       </Fragment>
         )
  }else{
    return( 
      <Fragment>
        
        <div>
           <div id="main-navbar" className="navbar" >
             <h2 className="logo">Login to watch out flights or sign-up if you don't have an account</h2>
             <nav>
               <ul>
               <li  onClick={()=>setformStatus("1")}>
                 Flights
               </li>
               <li onClick={()=>setformStatus("2")}>
                 Cart
               </li>
              
               </ul>
             </nav>
           </div>
         </div>
     
     
        <section class="cards">
           {Cart.map( (flight )  => {
            return(
              <div>
           <Flight flight={flight} ></Flight> 
              <button onClick={()=>addToCart(flight)}> Add to Cart</button>
              </div>
            
            )
           }
           
           )}
       </section>
       </Fragment>
         )
  }
  
 }
}

export default List;
