import './Api.css'
import axios from 'axios';
import { useState , useRef ,useEffect } from 'react';
import default1 from './default';
const Api = ()=>{
   
    const [data, setdata] = useState([]);
      console.log(data.forecast.forecastday[0].day.maxtemp_c)
  const london = ()=>{
    const option = {
        method: 'GET',
        url: 'https://weatherapi-com.p.rapidapi.com/forecast.json',
        params: {q: 'London', days: '7'},
        headers: {
          'X-RapidAPI-Key': 'c5e1fa75e8msh433e8726817dda3p16016cjsn33723a3074ec',
          'X-RapidAPI-Host': 'weatherapi-com.p.rapidapi.com'
        }
      };
      axios.request(option).then(function (response) {
             
        setdata(response.data)
       
    }).catch(function (error) {
        console.error(error);
    });
  
 
    }
    
     
         
      
   
  

      
       
      


  
 
    if(data==[]){
      return(<div>
        <div>
                    <button onClick={()=>{london()}}>London</button>
                    <button >New-York</button>
                    <button>Button</button>
                </div>
                     <h1>Check the forecast in those locations before you order a ticket </h1>
        </div>)
    }else return this.data.length ?

<div>
<div>
            <button onClick={()=>{london()}}>London</button>
            <button >New-York</button>
            <button>Button</button>
        </div>
                <div id="api_card">
               <h1>{data.forecast.forecastday[0].date}: {data.forecast.forecastday[0].day.maxtemp_c} °C</h1>
                <h1>{data.forecast.forecastday[1].date}: {data.forecast.forecastday[1].day.maxtemp_c} °C</h1>
                <h1>{data.forecast.forecastday[2].date}: {data.forecast.forecastday[2].day.maxtemp_c} °C</h1>
                </div>
</div>
       
   : null  
    
}

export default Api;
