import { useState , useRef ,useEffect, Fragment} from 'react';
import axios from 'axios';
import Flight from '../Flight/Flight';
import './List.css';
const List = ()=> {
  
  const [list,setlist] = useState([]);
  const [isLoading, setLoading] = useState(true);
  const [formStatus, setformStatus] = useState("1"); 
  const [Cart,setCart] = useState([]);
  useEffect(()=>{
    axios.get('http://localhost:8000/flight/')
    .then(function (response) {
     setlist(response.data) 
     setLoading(false)
    })

  });


  const inputChange = event => {
     let result = [];
     list.forEach(item =>{
      if(item.name.toLowerCase().includes(event.target.value)){result.push(item)}
     })
     
     setlist(result);
  };

const addToCart =((item)=>{
console.log(item);
setCart ( (Cart ) => {
  return [item , ...Cart];

});

})
 
 if(isLoading) {
  return (
    <h1>Loading</h1>
      )
 }else{
  if(formStatus==1){
    return( 
      <Fragment>
      
        <div>
           <div id="main-navbar" className="navbar" >
             <h2 className="logo">Login to watch out flights or sign-up if you don't have an account</h2>
             <nav>
               <ul>
               <li  onClick={()=>setformStatus("1")}>
                 Flights
               </li>
               <li onClick={()=>setformStatus("2")}>
                 Cart
               </li>
              
               </ul>
             </nav>
           </div>
         </div>
     
         <input  onChange={inputChange}></input>
        <section class="cards">
           {list.map( (flight )  => {
            return(
              <div>
           <Flight flight={flight} ></Flight> 
              <button onClick={()=>addToCart(flight)}> Add to Cart</button>
              </div>
            
            )
           }
           
           )}
       </section>
       </Fragment>
         )
  }else{
    return( 
      <Fragment>
        
        <div>
           <div id="main-navbar" className="navbar" >
             <h2 className="logo">Login to watch out flights or sign-up if you don't have an account</h2>
             <nav>
               <ul>
               <li  onClick={()=>setformStatus("1")}>
                 Flights
               </li>
               <li onClick={()=>setformStatus("2")}>
                 Cart
               </li>
              
               </ul>
             </nav>
           </div>
         </div>
     
     
        <section class="cards">
           {Cart.map( (flight )  => {
            return(
              <div>
           <Flight flight={flight} ></Flight> 
              <button onClick={()=>addToCart(flight)}> Add to Cart</button>
              </div>
            
            )
           }
           
           )}
       </section>
       </Fragment>
         )
  }
  
 }
}

export default List;
