import { useState , useRef ,useEffect, Fragment} from 'react';
import axios from 'axios';
import Flight from '../Flight/Flight';
import './List.css';
const List = ()=> {
  
  const [list,setlist] = useState([]);
  const [isLoading, setLoading] = useState(true);
  const [formStatus, setformStatus] = useState("1"); 
  const [Cart,setCart] = useState([]);
  useEffect(()=>{
    axios.get('http://localhost:8000/flight/')
    .then(function (response) {
     setlist(response.data) 
     setLoading(false)
    })

  });


  const addToCart = (item) => { 
    
    setCart ( (prevCart ) => {

      return [item , ...prevCart];
     
    });
  
  };

 
 if(isLoading) {
  return (
    <h1>Loading</h1>
      )
 }else{
  if(formStatus==1){
    return( 
      <Fragment>
        
        <div>
           <div id="main-navbar" className="navbar" >
             <h2 className="logo">Login to watch out flights or sign-up if you don't have an account</h2>
             <nav>
               <ul>
               <li  onClick={()=>setformStatus("1")}>
                 Flights
               </li>
               <li onClick={()=>setformStatus("2")}>
                 Cart
               </li>
              
               </ul>
             </nav>
           </div>
         </div>
     
     
        <section class="cards">
           {list.map( (flight )  => 
           <Flight flight={flight} addToCart={addToCart}></Flight>  
           )}
       </section>
       </Fragment>
         )
  }else{
    return(<h1>Cart</h1>)
  }
  
 }
}

export default List;
